import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { makeStyles } from '@material-ui/core';
import { TrendingCoins } from '../../config/api';
import { OracleContextState } from '../../OracleContext';
import AliceCarousel from 'react-alice-carousel';
import { Link } from 'react-router-dom';



const useStyles = makeStyles((theme) => ({
    carousel: {
        height: "50%",
        display: "flex",
        alignItems: "center",
    },
    symbolStyles: {
        color: "#fff",
        fontWeight: "bold",
    },
}));

const Carousel = () => {
    const [trending, setTrending] = useState([]);
    const classes = useStyles();
    const { currency } = OracleContextState();

    // TO FETCH TRENDING COINS 
    const trendingCoins = async () => {
        const { data } = await axios.get(TrendingCoins(currency))

        setTrending(data);
    };

    console.log(trending)

    useEffect(() => {
        trendingCoins();
    }, [currency]);

    // ITEMS CONTAINED IN CAROUSEL 

    const items = trending.map((crypto) => {
        return (
            <Link
                className={classes.carouselItem}
                // TO GO TO COIN PAGE ID FROM CAROUSEL 
                to={`/coin/${crypto.id}`}
            >
                <img
                    src={crypto?.image}
                    alt={crypto.name}
                    height="80"
                    style={{ marginBottom: 10 }}
                />
                <span className={classes.symbolStyles}>{crypto?.symbol}</span>
            </Link>
        );
    });

    // TO MAKE CAROUSEL RESPONSIVE 
    const responsive = {
        0: {
            items: 2,
        },
        512: {
            items: 4,
        },
    };

    // CAROUSEL SETTINGS 
    return (
        <div className={classes.carousel}>
            <AliceCarousel
                mouseTracking
                infinite
                autoPlayInterval={1000}
                animationDuration={1500}
                disableDotsControls
                disableButtonsControls
                responsive={responsive}
                autoPlay
                items={items}
            />
        </div>
    )
}

export default Carousel;
