import { makeStyles, Typography } from "@material-ui/core";
import axios from "axios";
import React, { useEffect, useState } from "react"
import { useParams } from "react-router-dom"
import { SingleCoin } from "../config/api";
import { OracleContextState } from "../OracleContext";
import CryptoInfo from "../components/CryptoInfo";

const CoinPage = () => {
    const { id } = useParams();
    const [crypto, setCrypto] = useState();
    const { currency, symbol } = OracleContextState();

    const fetchSingleCrypto = async () => {
        const { data } = await axios.get(SingleCoin(id));
        setCrypto(data);
    }

    useEffect(() => {
        fetchSingleCrypto();
    }, []);


    const useStyles = makeStyles((theme) => ({
        container: {
            display: "flex",
            [theme.breakpoints.down("md")]: {
                flexDirection: "column",
                alignItems: "center",
            }
        },
        divider: {
            width: "30%",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            marginTop: 20,
            borderRight: "2px solid #04D4F0",
            [theme.breakpoints.down("md")]: {
                width: "100%",
            }
        },
        heading: {
            fontFamily: "Montserrat",
            fontWeight: "600",
            marginBottom: 20,
        }
    }));

    const classes = useStyles();

    return (
        <div className={classes.container}>
            <div className={classes.divider}>
                <img
                    src={crypto?.image.large}
                    alt={crypto?.name}
                    height="200"
                    style={{ marginBottom: 20 }}
                />
                <Typography
                    variant="h3"
                    className={classes.heading}
                >
                    {crypto?.name}
                </Typography>
                <Typography>
                    {reactHTMLParser(crypto?.description.en.split90(". ")[0])}
                </Typography>
            </div>

            <CryptoInfo crypto={crypto} />
        </div>
    )
}

export default CoinPage
