import { makeStyles } from "@material-ui/core";
import axios from "axios";
import React, { useEffect, useState } from "react"
import { useParams } from "react-router-dom"
import { SingleCoin } from "../config/api";
import { OracleContextState } from "../OracleContext";
import CryptoInfo from "../components/CryptoInfo";

const CoinPage = () => {
    const { id } = useParams();
    const [crypto, setCrypto] = useState();
    const { currency, symbol } = OracleContextState();

    const fetchSingleCrypto = async () => {
        const { data } = await axios.get(SingleCoin(id));
        setCrypto(data);
    }

    useEffect(() => {
        fetchSingleCrypto();
    }, []);


    const useStyles = makeStyles((theme) => ({
        container: {
            display: "flex",
            [theme.breakpoints.down("md")]: {
                flexDirection: "column",
                alignItems: "center",
            }
        },
        cryptoChart: {
            width: "30%",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            marginTop: 25,
            borderRight: "2px solid #04D4F0",
        },
    }));

    const classes = useStyles();

    return (
        <div className={classes.container}>
            <div className={classes.cryptoInfo}>

            </div>
            <div className={classes.cryptoChart}>
                <CryptoInfo
                    crypto={crypto}
                />
            </div>

        </div>
    )
}

export default CoinPage
