import React, { useEffect, useState } from 'react'
import axios from 'axios';
import { CoinList } from '../config/api';
import { OracleContextState } from '../OracleContext';
import { Container, createTheme, LinearProgress, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, TextField, ThemeProvider } from '@material-ui/core';
import { Typography } from '@mui/material';
import { useNavigate } from "react-router-dom";


const Cryptotable = () => {
    const [crypto, setCrypto] = useState([]);
    const [loading, setLoading] = useState(false);
    const [search, setSearch] = useState("")
    const { currency } = OracleContextState();
    const navigate = useNavigate();


    const fetchCryptos = async () => {
        setLoading(true);
        const { data } = await axios.get(CoinList(currency))

        setCrypto(data);
        setLoading(false);
    };

    useEffect(() => {
        fetchCryptos();
        // eslint-disable-next-line
    }, [currency]);

    const darkTheme = createTheme({
        palette: {
            primary: {
                main: "#fff",
            },
            type: "dark",
        },
    });

    const handleSearch = () => {
        return crypto.filter(
            (crypto) =>
                crypto.name.toLowerCase().includes(search) ||
                crypto.symbol.toLowerCase().includes(search)
        );
    };

    return (
        <ThemeProvider theme={darkTheme}>
            <Container
                style={{
                    textAlign: "center"
                }}
            >
                <Typography
                    variant='h4'
                    style={{
                        margin: 20,
                        fontFamily: "Montserrat"
                    }}
                >
                    Currency Prices By Market Cap
                </Typography>
                <TextField
                    label="Search For Your Favorite Crypto"
                    variant="outlined"
                    style={{
                        width: "80%",
                        marginBottom: 20
                    }}
                    onChange={(e) => setSearch(e.target.value)}
                />
                <TableContainer>
                    {
                        loading ? (
                            <LinearProgress style={{ backgroundColor: "#04D4F0" }} />
                        ) : (
                            <Table>
                                <TableHead style={{ backgroundColor: "#04D4F0" }}>
                                    <TableRow>
                                        {["Name", "Price", "24h%", "Market Cap", "Circulating Supply"].map((tableHead) => (
                                            <TableCell
                                                style={{
                                                    color: "#111",
                                                    fontWeight: "600",
                                                    fontSize: 16,
                                                    fontFamily: "Montserrat"
                                                }}
                                                key={tableHead}
                                                align={tableHead === "Name" ? "" : "right"}
                                            >
                                                {tableHead}
                                            </TableCell>
                                        ))}
                                    </TableRow>
                                </TableHead>
                                <TableBody>
                                    {handleSearch().map((row) => {
                                        const priceChange = row.price_change_percentage_24h >= 0;
                                        return (
                                            <TableRow
                                                onClick={() => navigate(`/coins/${row.id}`)}
                                                className={classes.row}
                                                key={row.name}
                                            >

                                            </TableRow>
                                        )
                                    })}

                                </TableBody>
                            </Table>
                        )}
                </TableContainer>


            </Container>
        </ThemeProvider >
    )
}

export default Cryptotable;
